import React, { useState, useEffect, useRef } from "react";
import WaveSurfer from "wavesurfer.js";
import RegionsPlugin from "wavesurfer.js/dist/plugins/regions.esm.js";
import "./App.css";

const API_URL = "https://audio-transcription-editor-13c6d97d2e6b.herokuapp.com";

function App() {
    const [file, setFile] = useState(null);
    const [transcription, setTranscription] = useState(null);
    const waveformRef = useRef(null);
    const wavesurfer = useRef(null);
    const [playing, setPlaying] = useState(false);
    const [editableWords, setEditableWords] = useState([]);
    const [showEditModal, setShowEditModal] = useState(false);
    const [currentWord, setCurrentWord] = useState(null);
    const [timeRange, setTimeRange] = useState({ start: 0, end: 0 });
    const [duration, setDuration] = useState(0);
    const [showQCWord, setShowQCWord] = useState(null);
    const [loadingMessage, setLoadingMessage] = useState("");
    const [isLoading, setIsLoading] = useState(false);
    const regionRef = useRef(null);

    const handleFileChange = (e) => {
        setFile(e.target.files[0]);
    };

    const uploadFile = async () => {
        if (!file){
            alert("Please upload a file!");
            return
        }
        setIsLoading(true);
        setLoadingMessage("Transcribing audio...");

        try {
            const formData = new FormData();
            formData.append("file", file);

            const response = await fetch(`${API_URL}/upload`, {
                method: "POST",
                body: formData,
            });

            const data = await response.json();
            setTranscription(data);

            setLoadingMessage("Audio transcribed successfully!");
            setTimeout(() => setIsLoading(false), 1000);

            initializeWaveform(URL.createObjectURL(file));
        } catch (error) {
            alert("Error uploading file");
            setIsLoading(false);
        }
    };

    const initializeWaveform = (audioUrl) => {
        if (wavesurfer.current) {
          wavesurfer.current.destroy();
        }

        wavesurfer.current = WaveSurfer.create({
          container: waveformRef.current,
          waveColor: "#4a4a4a",
          progressColor: "#2196F3",
          cursorColor: "red",
          backend: "WebAudio",
          height: 100,
          plugins: [RegionsPlugin.create()],
          interact: false,
        });

        const wsRegions = wavesurfer.current.registerPlugin(RegionsPlugin.create());

        wavesurfer.current.load(audioUrl);

        wavesurfer.current.on("ready", () => {
          const duration = wavesurfer.current.getDuration();
          setDuration(duration);
          setTimeRange({ start: 0, end: duration });

          // Create initial region with proper configuration
          const region = wsRegions.addRegion({
            start: 0,
            end: duration,
            content: "Selected Region",
            color: "rgba(255, 0, 0, 0.1)",
            drag: true,
            resize: true,
            minLength: 0.1,
            handleStyle: {
              left: { backgroundColor: "red", width: "8px" },
              right: { backgroundColor: "red", width: "8px" },
            },
          });

          regionRef.current = region;

          // Update time range when region changes
          region.on("update-end", () => {
            setTimeRange({
              start: region.start,
              end: region.end,
            });
          });

          // Handle region clicks using the regions plugin
          wsRegions.on("region-clicked", (region, e) => {
            e.stopPropagation();
            if (playing) {
              region.pause();
              setPlaying(false);
            } else {
              region.play();
              setPlaying(true);
            }
          });

          // Handle playback completion
          wavesurfer.current.on("finish", () => setPlaying(false));
        });
      };





    useEffect(() => {
        if (transcription) {
            const filtered = transcription.words.filter(
                (word) =>
                    // Check if word overlaps with selected region
                    (word.start_time / 1000 < timeRange.end) && 
                    (word.end_time / 1000 > timeRange.start)
            );
            setEditableWords(filtered);
        }
    }, [timeRange, transcription]);

      // Update togglePlay function
      const togglePlay = () => {
        if (!wavesurfer.current || !regionRef.current) return;

        if (playing) {
          wavesurfer.current.pause();
          setPlaying(false);
        } else {
          regionRef.current.play();
          setPlaying(true);
        }
      };

    const openEditModal = (word) => {
        setCurrentWord(word);
        setShowEditModal(true);
    };

    const handleEditSubmit = (e) => {
        e.preventDefault();
        const formData = new FormData(e.target);

        const updatedWord = {
            ...currentWord,
            word: formData.get("word"),
            start_time: Math.round(parseFloat(formData.get("start")) * 1000),
            end_time: Math.round(parseFloat(formData.get("end")) * 1000),
            edited: true,
        };

        const index = transcription.words.findIndex((w) => w === currentWord);
        let updatedWords = [...transcription.words];
        updatedWords[index] = updatedWord;
        setTranscription({ ...transcription, words: updatedWords });

        setShowEditModal(false);
    };

    const saveEdits = async () => {
        await fetch(`${API_URL}/update-json/${file.name}`, {
            method: "POST",
            headers: { "Content-Type": "application/json" },
            body: JSON.stringify(transcription),
        });
        alert("Edits Saved!");
    };

    const downloadJSON = async () => {
        try {
            const response = await fetch(`${API_URL}/download/${file.name}`);
            const blob = await response.blob();
            const url = window.URL.createObjectURL(blob);
            const a = document.createElement("a");
            a.href = url;
            a.download = `${file.name}_transcription.json`;
            document.body.appendChild(a);
            a.click();
            window.URL.revokeObjectURL(url);
            document.body.removeChild(a);
        } catch (error) {
            alert("Error downloading JSON");
        }
    };

    return (
        <div className="App">
            <h2>BKB Transcription Editor</h2>
            <div className="controls">
                <div className="file-input-container">
                    <input 
                    type="file" 
                    id="fileInput" 
                    onChange={handleFileChange} 
                    className="file-input"
                    />
                    <label htmlFor="fileInput" className="custom-file-upload">
                    üìÅ Choose File
                    </label>
                    <span className="file-name">
                    {file ? file.name : "No file selected"}
                    </span>
                </div>
                <button onClick={uploadFile}>Upload & Transcribe</button>
                </div>
            {isLoading && (
                <div className="loading-overlay">
                    <div className="loading-spinner"></div>
                    <p>{loadingMessage}</p>
                </div>
                )}
            <div ref={waveformRef} className="waveform-container"></div>

            {transcription && (
                <div className="time-controls">
                    <div className="slider-container">
                        <label>Region Start (ms):</label>
                        <input
                            type="number"
                            step="1"
                            name="start"
                            value={Math.round(timeRange.start * 1000)}
                            readOnly
                            min="0"
                            max={Math.round(duration * 1000)}
                        />
                        <label>Region End (ms):</label>
                        <input
                            type="number"
                            step="1"
                            name="end"
                            value={Math.round(timeRange.end * 1000)}
                            readOnly
                            min="0"
                            max={Math.round(duration * 1000)}
                        />
                        <button onClick={togglePlay}>
                            {playing ? "‚è∏ Pause" : "‚ñ∂ Play Selection"}
                        </button>
                    </div>
                </div>
            )}

            {transcription && (
                <div className="transcription-container">
                    <div className="toolbar">
                        <button onClick={saveEdits} className="save-button">
                            üíæ Save All Changes
                        </button>
                        <button onClick={downloadJSON} className="download-button">
                            ‚¨á Download JSON
                        </button>
                    </div>

                    <div className="words-list">
                        {transcription.words.map((word, index) => (
                            <div
                                key={index}
                                className={`word ${
                                    editableWords.includes(word) ? "editable" : ""
                                }`}
                            >
                                <span className="word-text">
                                    {word.word}
                                    <span className="timestamps">
                                        ({word.start_time}ms - {word.end_time}ms)
                                        {word.edited && (
                                            <span className="edited-badge"> (Edited)</span>
                                        )}
                                    </span>
                                    <span 
                                    className={`qc-status ${word.qc ? 'pass' : 'fail'}`}
                                    onClick={() => setShowQCWord(word.qc_word)}
                                    onMouseEnter={(e) => e.target.style.color = 'blue'}
                                    onMouseLeave={(e) => e.target.style.color = ''}
                                >
                                    QC: {word.qc ? "‚úî Pass" : "‚ùå Fail"}
                                </span>
                                </span>

                                {editableWords.includes(word) && (
                                    <button
                                        onClick={() => openEditModal(word)}
                                        className="edit-button"
                                    >
                                        ‚úè Edit
                                    </button>
                                )}
                            </div>
                        ))}
                    </div>
                    {showQCWord && (
                <div className="modal-backdrop">
                    <div className="modal">
                        <h3>QC Transcribed Word</h3>
                        <p>{showQCWord}</p>
                        <button onClick={() => setShowQCWord(null)} className="cancel-button">
                            Close
                        </button>
                    </div>
                </div>
            )}
                    {showEditModal && (
                        <div className="modal-backdrop">
                            <div className="modal">
                                <h3>Edit Word</h3>
                                <form onSubmit={handleEditSubmit}>
                                    <div className="form-group">
                                        <label>Word:</label>
                                        <input
                                            name="word"
                                            defaultValue={currentWord.word}
                                            required
                                        />
                                    </div>

                                    <div className="form-group">
                                        <label>Start (seconds):</label>
                                        <input
                                            name="start"
                                            type="number"
                                            step="0.001"
                                            defaultValue={currentWord.start_time / 1000}
                                            required
                                        />
                                    </div>

                                    <div className="form-group">
                                        <label>End (seconds):</label>
                                        <input
                                            name="end"
                                            type="number"
                                            step="0.001"
                                            defaultValue={currentWord.end_time / 1000}
                                            required
                                        />
                                    </div>

                                    <div className="modal-actions">
                                        <button type="submit" className="save-button">
                                            üíæ Save
                                        </button>
                                        <button
                                            type="button"
                                            onClick={() => setShowEditModal(false)}
                                            className="cancel-button"
                                        >
                                            ‚úñ Cancel
                                        </button>
                                    </div>
                                </form>
                            </div>
                        </div>
                    )}
                </div>
            )}
        </div>
    );
}

export default App;
